mapper.select:
SELECT FREEPHOTO
FROM FREEBOARD
WHERE FREFER = #{fRefer}

mapper.update:
UPDATE FREEBOARD
SET
    FSUBJECT = #{fSubject},
    FCONTENT = #{fContent},
    FREEPHOTO = #{freePhoto}
WHERE FREFER = #{fRefer}

UPDATE FREEBOARD
SET READNUM = READNUM + 1
WHERE FREFER = #{frefer}

mapper.select:

SELECT COUNT(frerefer)
FROM FREEBOARDRE
WHERE frefer = #{frefer}

mapper.update:
UPDATE FREEBOARDRE
SET
    FRECONTENT = #{frecontent}
WHERE FREFER = #{frefer}
