mapper.delete:
DELETE
FROM PARTYUSER
WHERE EMAIL = #{email} and PARTYCODE = #{partyCode}

mapper.select:
SELECT P.PARTYCODE, P.PARTYNAME, P.PARTYSCORE, P.CREATEDATE, P.LOGOPIC FROM PARTY P JOIN HOBBY H ON P.HOBBYCODE = H.HOBBYCODE WHERE P.HOBBYCODE IN
#{hobby}
ORDER BY P.PARTYCODE DESC OFFSET (#{cri.currPage}-1) * #{cri.amount} ROWS FETCH NEXT #{cri.amount} ROWS ONLY
