mapper.delete:
DELETE FROM PARTYUSER
WHERE EMAIL=#{EMAIL}
AND PARTYCODE=#{PARTYCODE}

mapper.update:

UPDATE PARTY
SET LOGOPIC = #{LOGOPIC}
WHERE PARTYCODE = #{PARTYCODE}


UPDATE PARTY
SET PARTYNAME = #{PARTYNAME},
PARTYPROFILE = #{PARTYPROFILE},
LOCALCODE = #{LOCALCODE},
HOBBYCODE = #{HOBBYCODE}
WHERE PARTYCODE = #{PARTYCODE}


UPDATE PARTY
SET PARTYBANNED = -1
WHERE PARTYCODE = #{PARTYCODE}


UPDATE PARTYUSER
SET AUTHCODE = #{AUTHCODE}
WHERE EMAIL = #{EMAIL}
AND PARTYCODE = #{PARTYCODE}


UPDATE PARTYUSER
SET AUTHCODE = 1
WHERE EMAIL = #{EMAIL}
AND PARTYCODE = #{PARTYCODE}
