mapper.update:
UPDATE PARTYFREEBOARD
SET
    PFSUBJECT = #{pfsubject},
    PFCONTENT = #{pfcontent}
WHERE PFREFER = #{pfrefer}

UPDATE PARTYFREERE
SET
    PFRECONTENT = #{pfrecontent}
WHERE partycode = #{partycode} and pfrerefer = #{pfrerefer}

mapper.delete:
DELETE
FROM PARTYFREERE
WHERE PFREREFER=#{pfrerefer} and partycode = #{partycode}
