mapper.update:
UPDATE QNABOARD
SET
    QSUBJECT = #{qsubject},
    QCONTENT = #{qcontent}
WHERE QREFER = #{qrefer}

UPDATE QNABOARD
SET READNUM = READNUM + 1
WHERE QREFER = #{qrefer}

mapper.select:

SELECT COUNT(qrerefer)
FROM QNABOARDRE
WHERE qrefer = #{qrefer}

mapper.update:
UPDATE QNABOARDRE
SET
    QRECONTENT = #{qrecontent}
WHERE QREFER = #{qrefer}
